  `isbn` varchar(20) NOT NULL,
  `publication_date` date DEFAULT NULL,
  `price` decimal(10,2) NOT NULL,
  `stock_quantity` int unsigned DEFAULT '0',
  `author_id` int DEFAULT NULL,
  `publisher_id` int DEFAULT NULL,
  `genre_id` int DEFAULT NULL,
  `is_featured` tinyint(1) DEFAULT NULL,
  `is_available` tinyint(1) DEFAULT NULL,
  `edition_number` tinyint(1) DEFAULT NULL,
  `page_count` bigint DEFAULT NULL,
  `cover_image_binary` binary(100) DEFAULT NULL,
  `cover_image_varbinary` varbinary(200) DEFAULT NULL,
  `abstract_blob` blob,
  `sample_chapter_mediumblob` mediumblob,
  `notes_tinyblob` tinyblob,
  `full_text_longblob` longblob,
  `flags` bit(4) DEFAULT NULL,
  `series_code` char(10) DEFAULT NULL,
  `volume_code` char(5) DEFAULT NULL,
  `last_updated` datetime DEFAULT NULL,
  `discount_rate` decimal(3,2) DEFAULT NULL,
  `special_price` decimal(5,2) DEFAULT NULL,
  `average_rating` double DEFAULT NULL,
  `binding_type` enum('Hardcover','Paperback','E-book') DEFAULT NULL,
  `weight_grams` float DEFAULT NULL,
  `internal_id` int DEFAULT NULL,
  `catalog_id` mediumint DEFAULT NULL,
  `shelf_number` tinyint DEFAULT NULL,
  `inventory_code` smallint DEFAULT NULL,
  `metadata` json DEFAULT NULL,
  `keywords` set('Fiction','Mystery','Thriller','Science Fiction','Fantasy') DEFAULT NULL,
  `description_text` text,
  `long_description_mediumtext` mediumtext,
  `short_summary_tinytext` tinytext,
  `full_review_longtext` longtext,
  `last_modified` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `product_code` varchar(50) DEFAULT NULL,
  `ean_code` varchar(13) DEFAULT NULL,
  PRIMARY KEY (`book_id`),
  UNIQUE KEY `isbn` (`isbn`),
  KEY `fk_author_id` (`author_id`),
  KEY `fk_publisher_id` (`publisher_id`),
  KEY `fk_genre_id` (`genre_id`),
  CONSTRAINT `fk_author_id` FOREIGN KEY (`author_id`) REFERENCES `Authors` (`author_id`),
  CONSTRAINT `fk_genre_id` FOREIGN KEY (`genre_id`) REFERENCES `Genres` (`genre_id`),
  CONSTRAINT `fk_publisher_id` FOREIGN KEY (`publisher_id`) REFERENCES `Publishers` (`publisher_id`)
